  LightboxOptions = (function() {
    function LightboxOptions() {
      this.fadeDuration = 500;
      this.fitImagesInViewport = true;
      this.labelImage = "Image";
      this.labelOf = "of";
      this.resizeDuration = 700;
      this.showImageNumberLabel = true;
      this.wrapAround = false;
